mapper.insert:
INSERT INTO exhibition(
	admin_id, exhibition_title, exhibition_content, create_date, update_date
) VALUES(
	#{adminId}, #{exhibitionTitle}, #{exhibitionContent}, now(), now()
)

mapper.select:
SELECT exhibition_no exhibitionNo, admin_id adminId, exhibition_title exhibitionTitle, create_date createDate FROM exhibition
exhibition_title LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}

mapper.update:
UPDATE exhibition SET
	exhibition_title = #{exhibitionTitle}, exhibition_content = #{exhibitionContent}, update_date = now()
WHERE exhibition_no = #{exhibitionNo}
