mapper.delete:
DELETE FROM experience_select
WHERE experience_select_id = #{experienceSelectId}
AND traveler_id = #{travelerId}

DELETE FROM room_select
WHERE room_select_id = #{roomSelectId}
AND traveler_id = #{travelerId}

mapper.select:
SELECT e.experience_select_id, e.traveler_id, e.experience_select_title, t.traveler_name, e.create_date FROM traveler t INNER JOIN experience_select e ON t.traveler_id = e.traveler_id
experience_select_title LIKE '%${searchTitle}%'
ORDER BY e.experience_select_id DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}

SELECT r.room_select_id, r.traveler_id, r.room_select_title, t.traveler_name, r.create_date FROM traveler t INNER JOIN room_select r ON t.traveler_id = r.traveler_id
room_select_title LIKE '%${searchTitle}%'
ORDER BY r.room_select_id DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}
