mapper.delete:
DELETE FROM experience_select
WHERE experience_select_id = #{experienceSelectId}
AND traveler_id = #{travelerId}

mapper.select:
SELECT experience_select_id experienceSelectId, traveler_id travelerId, experience_select_title experienceSelectTitle, create_date createDate FROM experience_select
experience_select_title LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}

SELECT room_select_id roomSelectId, traveler_id travelerId, room_select_title roomSelectTitle, create_date createDate FROM room_select
room_select_title LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}
