mapper.select:
SELECT customer_id customerId, customer_name customerName, customer_email customerEmail, customer_phonenum customerPhonenum, active Active, create_date createDate, update_date updateDate FROM customer
customer_id LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}

SELECT ceo_id ceoId, ceo_name ceoName, ceo_email ceoEmail, ceo_phonenum ceoPhonenum, ceo_license ceoLicense, create_date createDate, update_date updateDate FROM ceo
ceo_id LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}
