mapper.delete:
DELETE FROM room_select
WHERE room_select_id = #{roomSelectId}
AND traveler_id = #{travelerId}

mapper.select:
SELECT room_select_id roomSelectId, traveler_id travelerId, room_select_title roomSelectTitle, create_date createDate FROM room_select
room_select_title LIKE '%${searchTitle}%'
ORDER BY create_date DESC LIMIT #{beginRow}, #{ROW_PER_PAGE}
